import React, { useEffect, useState } from 'react';
import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';

const App = () => {
  const [transaction, setTransaction] = useState([]);
  const [totalSale, setTotalSale] = useState(0);
  const [totalSoldItems, setTotalSoldItems] = useState(0);
  const [totalNotSoldItems, setTotalNotSoldItems] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const fetchTransaction = async () => {
    try {
      const response = await fetch('https://thingproxy.freeboard.io/fetch/https://s3.amazonaws.com/roxiler.com/product_transaction.json');
      const data = await response.json();
      console.log('Fetched data:', data);
      setTransaction(data);

      // Calculate statistics
      const totalSaleAmount = data.reduce((acc, curTran) => acc + (curTran.sold ? curTran.price : 0), 0);
      const totalSold = data.filter(curTran => curTran.sold).length;
      const totalNotSold = data.filter(curTran => !curTran.sold).length;

      setTotalSale(totalSaleAmount);
      setTotalSoldItems(totalSold);
      setTotalNotSoldItems(totalNotSold);

    } catch (e) {
      setError(e.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTransaction();
  }, []);

  return (
    <div className="App container mt-4">
      <h1 className='p-3'>Transaction Data</h1>
      <table className='table table-striped'>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Description</th>
            <th>Price</th>
            <th>Category</th>
            <th>Sold</th>
            <th>Image</th>
          </tr>
        </thead>
        <tbody>
          {transaction.length === 0 ? (
            <tr>
              <td colSpan="7" className="text-center">No Data Available</td>
            </tr>
          ) : (
            transaction.map((curTran) => {
              const { id, title, description, price, category, sold, image } = curTran;
              return (
                <tr key={id}>
                  <td>{id}</td>
                  <td>{title}</td>
                  <td>{description}</td>
                  <td>${price.toFixed(2)}</td>
                  <td>{category}</td>
                  <td>{sold ? "Yes" : "No"}</td>
                  <td><img src={image} alt={title} width="60" /></td>
                </tr>
              );
            })
          )}
        </tbody>
      </table>

      <div className='Statistic mt-4'>
        <p>Total Sale: ${totalSale.toFixed(2)}</p>
        <p>Total Sold Items: {totalSoldItems}</p>
        <p>Total Not Sold Items: {totalNotSoldItems}</p>
      </div>
    </div>
  );
};

export default App;
